import React, { useState, useEffect } from 'react';
import axios from 'axios';

const api = 'http://localhost:5001';

function App() {
  const [view, setView] = useState('login');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [userId, setUserId] = useState(null);
  const [elections, setElections] = useState([]);
  const [selectedElection, setSelectedElection] = useState(null);
  const [voteMsg, setVoteMsg] = useState('');
  const [results, setResults] = useState(null);
  const [newElection, setNewElection] = useState({ title: '', candidates: '' });
  const [error, setError] = useState('');

  useEffect(() => {
    if (view === 'dashboard') {
      axios.get(`${api}/elections`).then(res => setElections(res.data));
    }
  }, [view]);

  const handleRegister = () => {
    axios.post(`${api}/register`, { username, password })
      .then(() => setView('login'))
      .catch(e => setError(e.response?.data?.message || 'Registration failed'));
  };

  const handleLogin = () => {
    axios.post(`${api}/login`, { username, password })
      .then(res => { setUserId(res.data.user_id); setView('dashboard'); })
      .catch(e => setError(e.response?.data?.message || 'Login failed'));
  };

  const handleCreateElection = () => {
    const candidates = newElection.candidates.split(',').map(c => c.trim()).filter(Boolean);
    axios.post(`${api}/elections`, { title: newElection.title, candidates })
      .then(() => { setView('dashboard'); setNewElection({ title: '', candidates: '' }); })
      .catch(e => setError('Could not create election'));
  };

  const handleVote = (electionId, candidateId) => {
    axios.post(`${api}/vote`, { user_id: userId, election_id: electionId, candidate_id: candidateId })
      .then(() => setVoteMsg('Vote cast!'))
      .catch(e => setVoteMsg(e.response?.data?.message || 'Voting failed'));
  };

  const handleViewResults = (electionId) => {
    axios.get(`${api}/results/${electionId}`)
      .then(res => setResults(res.data))
      .catch(() => setResults(null));
  };

  return (
    <div style={{ maxWidth: 600, margin: '40px auto', fontFamily: 'Arial, sans-serif' }}>
      <h1>Online Voting System</h1>
      {view === 'login' && (
        <div>
          <h2>Login</h2>
          <input placeholder="Username" value={username} onChange={e => setUsername(e.target.value)} /><br />
          <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} /><br />
          <button onClick={handleLogin}>Login</button>
          <button onClick={() => setView('register')}>Register</button>
          {error && <div style={{color:'red'}}>{error}</div>}
        </div>
      )}
      {view === 'register' && (
        <div>
          <h2>Register</h2>
          <input placeholder="Username" value={username} onChange={e => setUsername(e.target.value)} /><br />
          <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} /><br />
          <button onClick={handleRegister}>Register</button>
          <button onClick={() => setView('login')}>Back to Login</button>
          {error && <div style={{color:'red'}}>{error}</div>}
        </div>
      )}
      {view === 'dashboard' && (
        <div>
          <h2>Welcome, {username}!</h2>
          <button onClick={() => setView('createElection')}>Create Election</button>
          <h3>Available Elections</h3>
          <ul>
            {elections.map(e => (
              <li key={e.id}>
                <b>{e.title}</b>
                <ul>
                  {e.candidates.map(c => (
                    <li key={c.id}>
                      {c.name}
                      <button style={{marginLeft:8}} onClick={() => handleVote(e.id, c.id)}>Vote</button>
                    </li>
                  ))}
                </ul>
                <button onClick={() => handleViewResults(e.id)}>View Results</button>
              </li>
            ))}
          </ul>
          {voteMsg && <div style={{color:'green'}}>{voteMsg}</div>}
          {results && (
            <div>
              <h4>Results</h4>
              <ul>
                {Object.entries(results).map(([name, count]) => (
                  <li key={name}>{name}: {count} votes</li>
                ))}
              </ul>
              <button onClick={() => setResults(null)}>Close Results</button>
            </div>
          )}
        </div>
      )}
      {view === 'createElection' && (
        <div>
          <h2>Create Election</h2>
          <input placeholder="Election Title" value={newElection.title} onChange={e => setNewElection({...newElection, title: e.target.value})} /><br />
          <input placeholder="Candidates (comma separated)" value={newElection.candidates} onChange={e => setNewElection({...newElection, candidates: e.target.value})} /><br />
          <button onClick={handleCreateElection}>Create</button>
          <button onClick={() => setView('dashboard')}>Cancel</button>
        </div>
      )}
    </div>
  );
}

export default App;
