import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Box, Button, TextField, Typography, Card, CardContent, CardActions, Grid, Alert, Snackbar, Container, Paper } from '@mui/material';
import DashboardLayout from './DashboardLayout';

const api = 'http://localhost:5001';

function App() {
  const [view, setView] = useState('login');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [userId, setUserId] = useState(null);
  const [elections, setElections] = useState([]);
  const [selectedElection, setSelectedElection] = useState(null);
  const [voteMsg, setVoteMsg] = useState('');
  const [results, setResults] = useState(null);
  const [newElection, setNewElection] = useState({ title: '', candidates: '' });
  const [error, setError] = useState('');

  useEffect(() => {
    if (view === 'dashboard') {
      axios.get(`${api}/elections`).then(res => setElections(res.data));
    }
  }, [view]);

  const handleRegister = () => {
    axios.post(`${api}/register`, { username, password })
      .then(() => { setError(''); setView('login'); })
      .catch(e => {
        if (e.response && e.response.data && e.response.data.message) {
          setError('Registration failed: ' + e.response.data.message);
        } else {
          setError('Registration failed: ' + (e.message || 'Unknown error'));
        }
      });
  };

  const handleLogin = () => {
    axios.post(`${api}/login`, { username, password })
      .then(res => { setUserId(res.data.user_id); setView('dashboard'); })
      .catch(e => setError(e.response?.data?.message || 'Login failed'));
  };

  const handleCreateElection = () => {
    const candidates = newElection.candidates.split(',').map(c => c.trim()).filter(Boolean);
    axios.post(`${api}/elections`, { title: newElection.title, candidates })
      .then(() => { setView('dashboard'); setNewElection({ title: '', candidates: '' }); })
      .catch(e => setError('Could not create election'));
  };

  const handleVote = (electionId, candidateId) => {
    axios.post(`${api}/vote`, { user_id: userId, election_id: electionId, candidate_id: candidateId })
      .then(() => setVoteMsg('Vote cast!'))
      .catch(e => setVoteMsg(e.response?.data?.message || 'Voting failed'));
  };

  const handleViewResults = (electionId) => {
    axios.get(`${api}/results/${electionId}`)
      .then(res => setResults(res.data))
      .catch(() => setResults(null));
  };

  // Navigation handler for DashboardLayout
  const handleNav = (target) => {
    if (target === 'logout') {
      setUserId(null);
      setUsername('');
      setPassword('');
      setView('login');
      setError('');
    } else {
      setView(target);
    }
  };

  // Login/Register forms (full page, centered)
  if (view === 'login' || view === 'register') {
    const isLogin = view === 'login';
    return (
      <Container maxWidth="xs" sx={{ minHeight: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
        <Paper elevation={3} sx={{ p: 4, width: 1 }}>
          <Typography variant="h5" align="center" gutterBottom>
            {isLogin ? 'Login' : 'Register'}
          </Typography>
          <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
            <TextField label="Username" value={username} onChange={e => setUsername(e.target.value)} fullWidth />
            <TextField label="Password" type="password" value={password} onChange={e => setPassword(e.target.value)} fullWidth />
            <Button variant="contained" color="primary" onClick={isLogin ? handleLogin : handleRegister} fullWidth>
              {isLogin ? 'Login' : 'Register'}
            </Button>
            <Button onClick={() => setView(isLogin ? 'register' : 'login')} fullWidth>
              {isLogin ? 'Register' : 'Back to Login'}
            </Button>
            {error && <Alert severity="error">{error}</Alert>}
          </Box>
        </Paper>
      </Container>
    );
  }

  // Dashboard & Create Election inside DashboardLayout
  return (
    <DashboardLayout selected={view} onSelect={handleNav} username={username}>
      {view === 'dashboard' && (
        <Box>
          <Typography variant="h4" sx={{ mb: 3, color: '#1976d2', fontWeight: 700 }}>Available Elections</Typography>
          <Grid container spacing={3}>
            {elections.map(e => (
              <Grid item xs={12} sm={6} md={4} key={e.id}>
                <Card elevation={2} sx={{ borderRadius: 3 }}>
                  <CardContent>
                    <Typography variant="h6" color="primary" gutterBottom>{e.title}</Typography>
                    <Typography sx={{ fontWeight: 500, color: '#555', mb: 1 }}>Candidates:</Typography>
                    <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1 }}>
                      {e.candidates.map(c => (
                        <Paper key={c.id} sx={{ p: 1, px: 2, borderRadius: 2, display: 'flex', alignItems: 'center', gap: 1, mb: 1 }}>
                          <Typography>{c.name}</Typography>
                          <Button size="small" variant="contained" color="success" onClick={() => handleVote(e.id, c.id)} sx={{ ml: 1 }}>
                            Vote
                          </Button>
                        </Paper>
                      ))}
                    </Box>
                  </CardContent>
                  <CardActions>
                    <Button variant="outlined" color="primary" fullWidth onClick={() => handleViewResults(e.id)}>
                      View Results
                    </Button>
                  </CardActions>
                </Card>
              </Grid>
            ))}
          </Grid>
          {voteMsg && <Snackbar open autoHideDuration={3000} onClose={() => setVoteMsg('')}><Alert severity="success">{voteMsg}</Alert></Snackbar>}
          {results && (
            <Paper elevation={4} sx={{ mt: 4, p: 3, borderRadius: 3 }}>
              <Typography variant="h6" color="primary" gutterBottom>Results</Typography>
              <Box component="ul" sx={{ listStyle: 'none', pl: 0 }}>
                {Object.entries(results).map(([name, count]) => (
                  <li key={name}>
                    <Typography sx={{ fontWeight: 600 }}>{name}: <span style={{ color: '#1976d2' }}>{count} votes</span></Typography>
                  </li>
                ))}
              </Box>
              <Button variant="contained" sx={{ mt: 2 }} onClick={() => setResults(null)}>Close Results</Button>
            </Paper>
          )}
        </Box>
      )}
      {view === 'createElection' && (
        <Box sx={{ maxWidth: 400 }}>
          <Typography variant="h5" sx={{ mb: 2 }}>Create Election</Typography>
          <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
            <TextField label="Election Title" value={newElection.title} onChange={e => setNewElection({ ...newElection, title: e.target.value })} fullWidth />
            <TextField label="Candidates (comma separated)" value={newElection.candidates} onChange={e => setNewElection({ ...newElection, candidates: e.target.value })} fullWidth />
            <Button variant="contained" color="primary" onClick={handleCreateElection}>Create</Button>
            <Button onClick={() => setView('dashboard')}>Cancel</Button>
          </Box>
        </Box>
      )}
    </DashboardLayout>
  );
}

export default App;
