import React, { useState, useEffect } from 'react';
import axios from 'axios';

const api = 'http://localhost:5001';

function App() {
  const [view, setView] = useState('login');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [userId, setUserId] = useState(null);
  const [elections, setElections] = useState([]);
  const [selectedElection, setSelectedElection] = useState(null);
  const [voteMsg, setVoteMsg] = useState('');
  const [results, setResults] = useState(null);
  const [newElection, setNewElection] = useState({ title: '', candidates: '' });
  const [error, setError] = useState('');

  useEffect(() => {
    if (view === 'dashboard') {
      axios.get(`${api}/elections`).then(res => setElections(res.data));
    }
  }, [view]);

  const handleRegister = () => {
    axios.post(`${api}/register`, { username, password })
      .then(() => { setError(''); setView('login'); })
      .catch(e => {
        if (e.response && e.response.data && e.response.data.message) {
          setError('Registration failed: ' + e.response.data.message);
        } else {
          setError('Registration failed: ' + (e.message || 'Unknown error'));
        }
      });
  };

  const handleLogin = () => {
    axios.post(`${api}/login`, { username, password })
      .then(res => { setUserId(res.data.user_id); setView('dashboard'); })
      .catch(e => setError(e.response?.data?.message || 'Login failed'));
  };

  const handleCreateElection = () => {
    const candidates = newElection.candidates.split(',').map(c => c.trim()).filter(Boolean);
    axios.post(`${api}/elections`, { title: newElection.title, candidates })
      .then(() => { setView('dashboard'); setNewElection({ title: '', candidates: '' }); })
      .catch(e => setError('Could not create election'));
  };

  const handleVote = (electionId, candidateId) => {
    axios.post(`${api}/vote`, { user_id: userId, election_id: electionId, candidate_id: candidateId })
      .then(() => setVoteMsg('Vote cast!'))
      .catch(e => setVoteMsg(e.response?.data?.message || 'Voting failed'));
  };

  const handleViewResults = (electionId) => {
    axios.get(`${api}/results/${electionId}`)
      .then(res => setResults(res.data))
      .catch(() => setResults(null));
  };

  return (
    <div style={{ maxWidth: 600, margin: '40px auto', fontFamily: 'Arial, sans-serif' }}>
      <h1>Online Voting System</h1>
      {view === 'login' && (
        <div>
          <h2>Login</h2>
          <input placeholder="Username" value={username} onChange={e => setUsername(e.target.value)} /><br />
          <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} /><br />
          <button onClick={handleLogin}>Login</button>
          <button onClick={() => setView('register')}>Register</button>
          {error && <div style={{color:'red'}}>{error}</div>}
        </div>
      )}
      {view === 'register' && (
        <div>
          <h2>Register</h2>
          <input placeholder="Username" value={username} onChange={e => setUsername(e.target.value)} /><br />
          <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} /><br />
          <button onClick={handleRegister}>Register</button>
          <button onClick={() => setView('login')}>Back to Login</button>
          {error && <div style={{color:'red'}}>{error}</div>}
        </div>
      )}
      {view === 'dashboard' && (
        <div>
          <div style={{display:'flex', justifyContent:'space-between', alignItems:'center', marginBottom:32}}>
            <h2 style={{margin:0}}>Welcome, <span style={{color:'#1976d2'}}>{username}</span>!</h2>
            <button
              onClick={() => setView('createElection')}
              style={{background:'#1976d2', color:'#fff', border:'none', borderRadius:6, padding:'10px 18px', fontWeight:600, fontSize:16, cursor:'pointer', boxShadow:'0 2px 8px #1976d230'}}>
              + Create Election
            </button>
          </div>
          <h3 style={{color:'#333', marginBottom:24}}>Available Elections</h3>
          <div style={{display:'grid', gridTemplateColumns:'repeat(auto-fit, minmax(300px, 1fr))', gap:24}}>
            {elections.map(e => (
              <div key={e.id} style={{background:'#fff', borderRadius:12, boxShadow:'0 2px 16px #1976d220', padding:24, display:'flex', flexDirection:'column', justifyContent:'space-between'}}>
                <div>
                  <div style={{fontSize:20, fontWeight:700, color:'#1976d2', marginBottom:12}}>{e.title}</div>
                  <div style={{marginBottom:10, fontWeight:500, color:'#555'}}>Candidates:</div>
                  <div style={{display:'flex', flexWrap:'wrap', gap:12}}>
                    {e.candidates.map(c => (
                      <div key={c.id} style={{background:'#f5f5f5', borderRadius:6, padding:'6px 12px', display:'flex', alignItems:'center', gap:8}}>
                        <span style={{fontSize:16}}>{c.name}</span>
                        <button
                          style={{background:'#43a047', color:'#fff', border:'none', borderRadius:4, padding:'4px 10px', fontWeight:600, fontSize:14, cursor:'pointer', transition:'background 0.2s'}}
                          onClick={() => handleVote(e.id, c.id)}>
                          Vote
                        </button>
                      </div>
                    ))}
                  </div>
                </div>
                <button
                  style={{marginTop:18, background:'#fff', color:'#1976d2', border:'1px solid #1976d2', borderRadius:6, padding:'8px 14px', fontWeight:600, fontSize:15, cursor:'pointer', alignSelf:'flex-end', transition:'background 0.2s'}}
                  onClick={() => handleViewResults(e.id)}>
                  View Results
                </button>
              </div>
            ))}
          </div>
          {voteMsg && <div style={{color:'#43a047', background:'#e8f5e9', padding:'12px 16px', borderRadius:6, marginTop:32, fontWeight:600}}>{voteMsg}</div>}
          {results && (
            <div style={{marginTop:32, background:'#fff', borderRadius:10, boxShadow:'0 2px 12px #1976d220', padding:24}}>
              <h4 style={{marginTop:0, color:'#1976d2'}}>Results</h4>
              <ul style={{listStyle:'none', padding:0}}>
                {Object.entries(results).map(([name, count]) => (
                  <li key={name} style={{marginBottom:8, fontSize:17}}>
                    <span style={{fontWeight:600}}>{name}</span>: <span style={{color:'#1976d2'}}>{count} votes</span>
                  </li>
                ))}
              </ul>
              <button
                style={{marginTop:16, background:'#1976d2', color:'#fff', border:'none', borderRadius:5, padding:'8px 16px', fontWeight:600, fontSize:15, cursor:'pointer'}}
                onClick={() => setResults(null)}>
                Close Results
              </button>
            </div>
          )}
        </div>
      )}
      {view === 'createElection' && (
        <div>
          <h2>Create Election</h2>
          <input placeholder="Election Title" value={newElection.title} onChange={e => setNewElection({...newElection, title: e.target.value})} /><br />
          <input placeholder="Candidates (comma separated)" value={newElection.candidates} onChange={e => setNewElection({...newElection, candidates: e.target.value})} /><br />
          <button onClick={handleCreateElection}>Create</button>
          <button onClick={() => setView('dashboard')}>Cancel</button>
        </div>
      )}
    </div>
  );
}

export default App;
